import React from 'react';
import './pad.css';

const DesignPad = (props) => {

    const dragStart = (ev) => {
        const target = ev.target;
        ev.dataTransfer.setData("cardId", target.id);

        setTimeout(() => {
          // be able to grab it so there is a reference to it and make it invisible
          // this function will give just enough delay to do that
          target.style.display = "none";
        }, 0);
      };

    return(
        <div className='designPad'
            id={props.id}
            draggable='true'
            onDragStart={dragStart}
            onDragOver={dragOver}
            >{props.children}</div>
    )
}
export default DesignPad;
