import React, { useState } from 'react';
import './pad.css';

const Pad = (props) => {
    const [data, setData] = useState({
        positions: new Array(9).fill(null)
    })

    const allowDrop = (ev) => {
        ev.preventDefault();
    }
    const drop = (ev) => {
        ev.preventDefault();
        const cardId = ev.dataTransfer.getData('cardId');
        const card = document.getElementById(cardId)
    }

    let padButtons = null;
    padButtons = data.positions.map((number,index) => {
        if(index > 0){
            return (
                <div className='buttonsPads' key={index} id={`div${index}`}>
                    {index}
                </div>
            )
        }else{
            return null;
        }
    });

    return(
        <div className='padDesign'>
        {padButtons}
        </div>
    )
}
export default Pad;
