import React, { useState } from "react";
import "./pad.css";

const Pad = (props) => {
  const [data, setData] = useState({
    positions: new Array(9).fill(null),
  });

  const allowDrop = (ev) => {
    ev.preventDefault();
  };
  const drop = (ev) => {
    ev.preventDefault();
    const cardId = ev.dataTransfer.getData("cardId");
    const card = document.getElementById(cardId);
    card.style.display = "block";

    ev.target.appendChild(card);
  };
  const dragOver = (ev) => {
      ev.preventDefault();
      ev.dataTransfer.setData('cardId', ev.target.id);
  }
  const dragStart = (ev) => {
      const target = ev.target;
      ev.dataTransfer.setData('cardId', target.id);

      setTimeout(() => {
        // be able to grab it so there is a reference to 
          target.style.display = 'none';
      },0)
  }

  let padButtons = null;
  padButtons = data.positions.map((number, index) => {
    if (index > 0) {
      return (
        <div
          className="buttonsPads"
          draggable='true'
          key={index}
          id={`div${index}`}
          onDrop={drop}
          onDragOver={dragOver}
          onDragStart={dragStart}
        >
          {index}
        </div>
      );
    } else {
      return null;
    }
  });

  return <div className="padDesign">{padButtons}</div>;
};
export default Pad;
