import React, { useState } from "react";
import "./pad.css";

const Pad = (props) => {
  const [data, setData] = useState({
    positions: new Array(9).fill(null),
  });


  const drop = (ev) => {
    ev.preventDefault();
    // transfer the id between this event, get the element and append it to the board
    const cardId = ev.dataTransfer.getData("cardId");
    const card = document.getElementById(cardId);
    card.style.display = "block";

    ev.target.appendChild(card);
  };
  const dragOver = (ev) => {
    ev.preventDefault();
  };


  let padButtons = null;
  padButtons = data.positions.map((number, index) => {
    if (index > 0 && !props.buttonState) {
      return (
        <div
          className="buttonsPads"
          draggable="true"
          key={index}
          id={`div${index}`}
          onDrop={drop}
          onDragOver={dragOver}

        >
          {index}
        </div>
      );
    } else if (index > 0 && props.buttonState) {
      return (
        <div className="buttonsPads" key={index} id={`div${index}`}>
          {index}
        </div>
      );
    } else {
      return null;
    }
  });

  return <div className="padDesign">{padButtons}</div>;
};
export default Pad;
